        _id: ID,
        firstname: String!
        lastname: String!
        age: Int!
    }

    type Mutation {
        createTask(input: TaskInput): Task
        createUser(input: UserInput): User
        deleteUser(_id: ID): User
        updateUser( _id: ID, input: UserInput): User
    }

    input TaskInput {
        title: String!,
        desc: String!,
        number: Int
    }

    input UserInput {
        firstname: String!
        lastname: String!
